INSERT INTO `kpi_list` VALUES ('731', '2560', 'ประเทศ', '49', null, 'ร้อยละของผู้ป่วย CKD ที่มีอัตราการลดลงของ eGFR<4 ml/min/1.73m2/yr', '<p>eGFR หมายถึง ค่าประมาณอัตราการกรองของไตที่คำนวณจาก serumcreatinineที่ตรวจโดยวิธี enzymatic methodและคำนวณโดยใช้สมการ CKD-EPI<br />\r\nผู้ป่วย CKD จะเก็บข้อเฉพาะผู้ป่วย CKD ระยะที่ 3-4 เท่านั้น หมายถึง ผู้ป่วยที่มีรหัสโรคเป็น N183-184, (N189 ที่มี 60 &gt;eGFR&ge;15) หรือ ( (E102, E112, E122, E132, E142 หรือ I12*, I13*, I151) ที่มี 60 &gt;eGFR &ge; 15 ) และมีการตรวจคำนวณ eGFR (LABTEST 15) &nbsp;&ge; 2 ค่า หากมีค่า eGFR 1 ค่าใน 1 ปี จะไม่ประเมิน KPI ข้อนี้ และ สัญชาติไทย (File PERSON: สัญชาติไทยรหัส 099) เท่านั้น</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'ร้อยละ', '', 'ปีงบประมาณ 60 = ≥65%', 'ผู้ป่วย CKD', null, null, null, null, null, '0', '0', '0', '0', '0', '0', null, '<p>1. เก็บทันที (real time แบบอัตโนมัติจากฐานข้อมูล 43 แฟ้ม) (ใช้ข้อมูลจาก HDC กระทรวงสาธารณสุข เป็นหลัก)<br />\r\n2. การคำนวณการเปลี่ยนแปลง eGFRต้องมี creatinine &ge; 2 ค่าในปีงบประมาณนั้นๆ<br />\r\n3. หน่วยที่ใช้ในการวัด เป็นร้อยละ<br />\r\n4. จากฐานข้อมูลโรงพยาบาลทุกสังกัดในพื้นที่กรุงเทพมหานคร</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>HDC กระทรวงสาธารณสุข / โรงพยาบาลทุกสังกัดในพื้นที่กรุงเทพมหานคร</p>\r\n', '', 'A = จำนวนผู้ป่วยโรคไตเรื้อรังระยะที่ 3-4 ที่มีอัตราการลดลงของ eGFR< 4 ml/min/1.73 m2/yr x 100', '', 'B = จำนวนผู้ป่วยโรคไตเรื้อรังระยะที่ 3-4', '', '', '', '', '', '', '', '', '=', '(A/B) x 100', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, '', 'ไตรมาส 2 และ 3 (ตามรอบการตรวจราชการปกติ)', '<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:497px\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\" style=\"width:172px\">\r\n			<p style